import React, { Component } from 'react';
import TodoItem from './TodoItem';


class TodoForm extends Component {
    state = {
        items: [],
        inputText: ''
    }

handleSubmit = e => {
    e.preventDefault ()
    console.log(this.state.inputText)
    this.setState({
        inputText: '',
        items: [...this.state.items, this.state.inputText]
    });
}

handleTyping = e => {
    this.setState({
        inputText: e.target.value
    })
}

handleDelete = selectedIndex => {

    // let itemsClone = [...this.state.items]
    // itemsClone = item.splice()

    const filteredItems = this.state.items.filter((item, index) => {
        return index !== selectedIndex
    })

    this.setState({
        items: filteredItems
    })
}

handleComplete = selectedIndex => {

    const filteredItems = this.state.items.filter((item, index) => {
        return index !== selectedIndex
    })

    this.setState({
        items: filteredItems
    })

}


    render() {
        const { inputText } = this.state
        return (
            <div className="m-3">
            <h1>To-do List</h1>
                    <form onSubmit={this.handleSubmit}>
                        <input 
                            placeholder="Task"
                            value={inputText}
                            onChange={this.handleTyping}
                            type="text"
                            name="message"
                        />
                        <button disabled={!inputText.length} type="submit"> Add Task </button>
                        {
                            inputText.length < 4 && inputText.length > 1 &&
                            <p className="text-danger">Must be more than 3 characters</p>
                        }

                        <ul>
                            {
                                this.state.items.map((item, index) => {
                                    return (
                                        <TodoItem id={index} key={index} handleDelete={this.handleDelete} item={item} />
                                    )
                                })
                            }
                        </ul>   


                    </form>
                </div>
        )
    }
}
export default TodoForm
