import React from 'react';

const TodoItem = ({ id, handleDelete, handleComplete, item }) => {

    const handleClick = () => {
        handleDelete(id)
    }
    const handleMarkDone = () => {
        handleComplete(id)
    }

    return (
        <li className="d-flex">
            <div className="" style={{ float: 'center', textDecoration: item.done ? "line-through" : null }} onClick={handleMarkDone}>{item.task} {handleClick}>[x] </div>
            {/* <div style={{ float: 'none' }} onClick={handleClick}>[x] </div> */}
        </li>
    )
}

export default TodoItem
