import React from 'react';

const TodoItem = ({ id, handleDelete, handleComplete, item }) => {

    const handleClick = () => {
        handleDelete(id)
    }
    const handleMarkDone = () => {
        handleComplete(id)
    }

    return (
        <li className="">
            <p style={{ textDecoration: item.done ? "line-through" : null }} onClick={handleMarkDone}>{item.task}</p>
            <a href='#' className="d-inline" onClick={handleClick}>[x] </a>
        </li>
    )
}

export default TodoItem
