import React from 'react';
import './App.css';
import TodoForm from './components/TodoForm.js';

class App extends React.Component {
  state = {
    items: []
  }

  addItem = (item) => {
    console.log(item)
    this.setState({
      items: []
    })
  }

  render() {

    return (
      <div className="App" >



        <TodoForm addItem={this.addItem}
        />

      </div>
    );

  }
}

export default App;
