import React from 'react';
import './App.css';
import TodoForm from './components/TodoForm.js';

class App extends React.Component {
  state = {
    items: []
  }

  addItem = (item) => {
    const newList = this.state.items
    const todo = {
      'task': item,
      'done': false
    }
    newList.push(todo)
    this.setState({
      items: newList
    })
  }

  render() {
    const { items } = this.state
    return (
      <div className="App" >



        <TodoForm items={items} addItem={this.addItem}
        />

      </div>
    );

  }
}

export default App;
