  state = {
    todos: [
      { id: 1, task: 'Walk the dog', done: false },
      { id: 2, task: 'Water the flower', done: true }
    ]
  }

  handleInput = e => {
    console.log('Hello Input')
  }
  addItem = () => {
    console.log('Hello Add Item')
  }

  render() {

    return (
      <div className="App" >
        <TodoItem addItem={this.addItem}>
          <TodoForm>

          </TodoForm>
        </TodoItem>
      </div>
    );

  }
}

export default App;
