import React from 'react';

const TodoItem = ({ id, handleDelete, handleComplete, item }) => {

    const handleClick = () => {
        handleDelete(id)
    }


    return (
        <li style={{ textDecoration: item.done ? "line-through" : null }} onClick={handleComplete}>
            {item.task}
            <a href='#' onClick={handleClick}>[x] </a>
        </li>
    )
}

export default TodoItem
