class App extends ReactComponent {
  state = {
    todos: [
      { id: 1, task: 'Walk the dog', done: false },
      { id: 2, task: 'Water the flower', done: true }
    ]
  }

  render() {

    return (
      <div className="App" >
        <TodoItem>
        </TodoItem>
      </div>
    );

  }
}

export default App;
